let burger=document.querySelector("#burger"),navToggle=document.querySelector("#navToggle");burger.addEventListener("click",function(){navToggle.classList.add("show")});let header=document.querySelector("#header"),intro=document.querySelector("#intro");function checkScroll(e,r){r<=e?header.classList.add("fixed"):header.classList.remove("fixed")}window.addEventListener("scroll",function(){var e=intro.clientHeight;checkScroll(window.scrollY,e)});